mapper.select:
SELECT
    count(ct.id)
FROM
    jl_check_task ct
LEFT JOIN jl_power_site ps ON ct.site_id = ps.id
LEFT JOIN sys_user u ON ct.checker = u.id


        checker = #{checker}


        AND check_detail like #{checkDetail}


        AND site_id like #{siteId}


        AND status like #{status}


;

SELECT
    ct.id AS ctid,
    u.username,
    check_detail,
    ps.site_name,
    ct.STATUS,
    addtime
FROM
    jl_check_task ct
LEFT JOIN jl_power_site ps ON ct.site_id = ps.id
LEFT JOIN sys_user u ON ct.checker = u.id


        checker = #{checker}


        AND check_detail like #{checkDetail}


        AND site_id like #{siteId}


        AND status like #{status}


LIMIT #{start},#{pageSize}
;
