mapper.select:
SELECT
    count(jor.id)
FROM
jl_operation_record jor
LEFT JOIN jl_power_site ps ON jor.site_id = ps.id
LEFT JOIN sys_user u ON u.id = jor.auditor


        auditor = #{auditor}


        AND site_id = #{siteId}


        AND operation_detail like #{operationDetail}


        AND (operation_time between  CONCAT(#{operationTimeBegin}, ' 00:00:00') and CONCAT(#{operationTimeEnd}, ' 23:59:59'))


;

SELECT jor.id as jorid, u.username, ps.site_name, operation_detail, operation_time FROM jl_operation_record jor LEFT JOIN jl_power_site ps ON jor.site_id = ps.id LEFT JOIN sys_user u ON u.id = jor.auditor
auditor = #{auditor}
AND site_id = #{siteId}
AND operation_detail like #{operationDetail}
AND (operation_time between CONCAT(#{operationTimeBegin}, ' 00:00:00') and CONCAT(#{operationTimeEnd}, ' 23:59:59'))
LIMIT #{start}, #{pageSize};
