mapper.select:
SELECT
    count(wr.id)
FROM
    jl_worksheet_record wr
LEFT JOIN jl_power_site ps ON wr.site_id = ps.id
LEFT JOIN sys_user u ON wr.operator_id = u.id


        site_id = #{siteId}


        AND status = #{status}


        AND operator_id = #{operatorId}


        AND worksheet_detail = #{worksheetDetail}


    ;

SELECT
    wr.id AS wrid,
    ps.site_name,
    wr. STATUS,
    u.username,
    worksheet_detail,
    add_time
FROM
    jl_worksheet_record wr
LEFT JOIN jl_power_site ps ON wr.site_id = ps.id
LEFT JOIN sys_user u ON wr.operator_id = u.id


        site_id = #{siteId}


        AND status = #{status}


        AND operator_id = #{operatorId}


        AND worksheet_detail = #{worksheetDetail}


LIMIT #{start}, #{pageSize};
